sys_variable_value.value:
(function execute(inputs, outputs) {
    var reqPayload = {};
    var propertiesPayload = {};
    var productRecordsList = [];
    var recordsObj = {};

    var productPropertiesList = [];
    var propertiesObj = {};
    var productSysId = inputs.pathParams.sysId;
    var productOffering = inputs.productOffering;
    var productOfferingCharacteristics = inputs.productOfferingCharacteristics;
    recordsObj.attributes = 
        {
           "type" : "Product2",
            "referenceId": productOffering.getValue('sys_id')
        };

    recordsObj.Name = productOffering.getValue('name');
    recordsObj.IsActive = productOffering.getValue('status') != 'published' ? false : true;
    recordsObj.Family = productOffering.getValue('u_family');
    recordsObj.Entity__c = "Flag";
    recordsObj.Description = productOffering.getValue('description');
    recordsObj.Reference_Id__c = productOffering.getValue('sys_id');

	productRecordsList.push(recordsObj);


    reqPayload.records = productRecordsList;
  gs.info("Request payload"+reqPayload);
  outputs.payload = reqPayload;
})(inputs, outputs);

