sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  gs.info('called from flow designer script');
   try { 
      var postRequest = new sn_ws.RESTMessageV2('Product Calatalog Characterstics', 'Post Product catalog characterstics');
      // postRequest.setAuthenticationProfile('OAuth 2.0', 'Oauth conf default_profile');
     var sfPayload = inputs.result;
     gs.info('sfPayload'+sfPayload);
     var parsedsfPayload = JSON.stringify(sfPayload);
     gs.info('passing into salesforce' + parsedsfPayload);     
     postRequest.setRequestHeader("Content-Type", "application/json");
       postRequest.setRequestBody(parsedsfPayload);
       var response = postRequest.execute();
            var responseObject = JSON.parse(response.getBody());
            gs.info(responseObject);
     var hasErrors = responseObject.hasErrors;
     gs.info('hasErrors'+hasErrors);
     if (hasErrors == false){       
     var firstResultId = responseObject.results[0].id;
     var firstRefernceId = responseObject.results[0].referenceId;
     gs.info('firstResultId' +firstResultId);
      gs.info('firstRefernceId' +firstRefernceId);
    // var productOff = new GlideRecord('sn_prd_pm_product_offering');

   //  productOff.addQuery('sys_id',firstRefernceId);
      //      productOff.query();
       //     if (productOff.next()) {
     //  var productOffering = inputs.productOffering;
              // productOffering.external_id = firstResultId;
            //  productOffering.update();
          //  }
       outputs.firstresultid = firstResultId;
       outputs.firstrefernceid = firstRefernceId;
     }
     else{
       gs.error('error occured in salesforce '+ responseObject.results[0]);
   // postRequest.setLogLevel('all');
     }
outputs.responseobject = responseObject;
 var httpStatus = response.getStatusCode();
     gs.info('after execution ' + response.getBody());
    // outputs.responsebody=responseBody;
}
catch(ex) {
 var message = ex.message;
  gs.error('caught error ' + message);
}
})(inputs, outputs);

