sys_variable_value.value:
(function execute(inputs, outputs) {
    var status;
    var result = {};
    var payload = {};

  var prodOffSysId = inputs.productOffering;
  gs.info('prodOffSysId'+ prodOffSysId);
    if (!prodOffSysId) {
        outputs.error = 'Missing Product Offering Sys_ID';
        outputs.statusCode = 422;
        return;
    }
    try {
        // Instantiate the script include
        var productOfferingChars = new CreateProductOfferingCharacteristics();
        // Call the CreateProdOfferingChars method with the product offering sys_id
        var productOfferingCharsResult = productOfferingChars.CreateProdOfferingChars(prodOffSysId);
        gs.info('productOfferingCharsResult ' + productOfferingCharsResult);
        // Set the result and status code
        result.records = JSON.parse(productOfferingCharsResult);
        //payload.records = JSON.parse(productOfferingCharsResult);
        statusCode = 200;
      gs.info('result ' + result);
     // gs.info('payload' +  payload);
    } catch (error) {
        result.error = "Error occurred: " + error.message;
      gs.error('Error ' +result.error);
        statusCode = 500;
    }

    outputs.result = result;
  //  outputs.payload =  payload;
    outputs.statuscode = statusCode;
})(inputs, outputs)

